import Form from "./Form.js";
import Backdrop from "./Backdrop.js";
import { useState } from 'react';

class AddTask extends Component {

	state = {
		formIsOpen: false
	}

	desactivateForm() {
		setModalIsOpen(false);
	}


	render() {
		return (
			<div>
				<Form AddNewTask={this.props.AddNewTask}/>
				<Backdrop />
			</div>
		)
	}
}

export default AddTask
