import './index.css';
import Todo from './Todo.js'
import { Component } from 'react';

class App extends Component {
  state = { 
    all_task: []
  }

  addTask() {
    this.state.all_task.push();
  }

  removeTask(index) {
    const ret = this.state.all_task.map((index, key) =>
    {
      return (
        index !== key
      )
    })
  }

  render() {  
    return (
      <div>
        <h1>My todos</h1>
        <Todo all_task={this.state.all_task} />
      </div>
    )
  }
}

export default App;
