import './index.css';
import React, { Component } from 'react';

class Todo extends Component {
	activateModal() {
		this.setState({isSetModal: true})
	}

	desactivateModal() {
		this.setState({isSetModal: false})
	}

  	render() {
	  	const render_tab = this.props.task_data.map((data, index) => {
		const retgreen = (
			<div className='greencard'>
			<tr key={index}>
				<h2>{data.task_name}</h2>
				<h3>{data.task_description}</h3>
				<div className='actions'>
					<button className='btn-green' onClick={() => this.props.removeFunction(index)}>Delete</button>
					<button className='btn-green' onClick={() => this.props.editFunction(index)}>Edit</button>
				</div>
			</tr>
	 		</div>
		)

		const retred = (	
			<div className='redcard'>
			<tr key={index}>
				<h2>{data.task_name}</h2>
				<h3>{data.task_description}</h3>
				<div className='actions'>
			  		<button className='btn-red' onClick={() => this.props.removeFunction(index)}>Delete</button>
					<button className='btn-red' onClick={() => this.props.editFunction(index)}>Edit</button>
				</div>
			</tr>
	 		</div>
		)
		if(data.task_status === 'done')
			return retgreen
		return retred
	  })
	  return <body>{render_tab}</body>
	}
}

export default Todo;
