import './index.css';
import React, { Component } from 'react';
import Backdrop from './Backdrop.js'
import Modal from './Modal.js'

class Todo extends Component {

	state = {
		ModalisOpen: false
	}

	activateModal() {
		this.setState({ModalisOpen: true})
	}

	desactivateModal() {
		this.setState({ModalisOpen: false})
		console.log(this.state.ModalisOpen)
	}

	deleteHandler() {
		this.activateModal()
	}

	editHandler() {
		this.activateModal()
	}

  	render() {
	  	const render_tab = this.props.task_data.map((data, index) => {
		const retgreen = (
			<li key={index}>
			<div className='greencard'>
				<h2>{data.task_name}</h2>
				<h3>{data.task_description}</h3>
				<div className='actions'>
					<button className='btn-green' onClick={() => this.deleteHandler(index)}>Delete</button>
					<button className='btn-green' onClick={() => this.editHandler(index)}>Edit</button>
				</div>
				{this.state.ModalisOpen && <Modal />}
				{this.state.ModalisOpen && <Backdrop closeModal={this.desactivateModal}/>}
	 		</div>
			</li>
		)

		const retred = (	
			<li key={index}>
			<div className='redcard'>
				<h2>{data.task_name}</h2>
				<h3>{data.task_description}</h3>
				<div className='actions'>
			  		<button className='btn-red' onClick={() => this.deleteHandler(index)}>Delete</button>
					<button className='btn-red' onClick={() => this.editHandler(index)}>Edit</button>
				</div>
				{this.state.ModalisOpen && <Modal />}
				{this.state.ModalisOpen && <Backdrop closeModal={this.desactivateModal}/>}
	 		</div>
			</li>
		)
		if(data.task_status === 'done')
			return retgreen
		return retred
	  })
	  return <body>{render_tab}</body>
	}
}

export default Todo;
