import React, {Component} from 'react'

class Form extends Component
{
	render() {
		const { task_name, task_description, task_status } = this.state;

		return (
		  <form>
			<label htmlFor="task_name">Name</label>
			<input
			  type="text"
			  name="task_name"
			  id="task_name"
			  value={task_name}
			  onChange={this.handleChange} />
			<label htmlFor="task_description">Description</label>
			<input
			  type="text"
			  name="task_description"
			  id="task_description"
			  value={task_description}
			  onChange={this.handleChange} />
			<label htmlFor="task_status">Status</label>
			<input
			  type="text"
			  name="task_status"
			  id="task_status"
			  value={task_status}
			  onChange={this.handleChange} />
			<input type="button" value="Add new Task" onClick={this.submitForm} />
		  </form>
		);
	}

	submitForm = () => {
		this.props.handleSubmit(this.state);
		this.setState(this.initialState)
	}

	handleChange = (event) => {
		const {name, value} = event.target

		this.setState({
			[name]: value,
		})
	}
}

export default Form
