import './index.css';
import React, { Component } from 'react';

class Todo extends Component {
  	render() {
	  	const render_tab = this.props.task_data.map((data, index) => {
		const retgreen = (
			<div className='greencard'>
				<h2>{data.task_name}</h2>
				<h3>{data.task_description}</h3>
				<div className='actions'>
					<button className='btn-green' onClick={() => data.removeFunction(index)}>Delete</button>
					<button className='btn-green' onClick={() => data.editFunction(index)}>Edit</button>
				</div>
	 		</div>
		)

		const retred = (	
			<div className='redcard'>
				<h2>{data.task_name}</h2>
				<h3>{data.task_description}</h3>
				<div className='actions'>
			  		<button onClick={() => data.removeFunction(data.task_id)}>Delete</button>
					<button onClick={() => data.editFunction(data.task_id)}>Edit</button>
				</div>
	 		</div>
		)
		if(data.task_status === 'done')
			return retgreen
		return retred
	  })
	  return <body>{render_tab}</body>
	}
}

export default Todo;
