import './index.css';
import React, { Component } from 'react';

class Todo extends Component {
  	render() {
	  	const render_tab = this.props.task_data.map((data, index) => {
		return (
			{data}
		  )
	  })
	  return <body>{render_tab}</body>
	}
}



const redTask = (props) => {

	const ret_map = props.task_data.map((data, index) => {
		return (
		<tr key={index}>
			<td>{data.task_name}</td>
			<td>{data.task_description}</td>
			<td>{data.task_status}</td>
			<th>
				<button onClick={() => props.removeTask(index)}>Delete</button>
				<button onClick={() => props.editTask(index)}>Edit</button>
			</th>
		</tr> 
		)
	})
	return <tbody>{ret_map}</tbody>
}

const greenTask = (props) => {

	const ret_map = props.task_data.map((data, index) => {
		return (
		<tr key={index}>
			<td>{data.task_name}</td>
			<td>{data.task_description}</td>
			<td>{data.task_status}</td>
			<th>
				<button onClick={() => props.removeTask(index)}>Delete</button>
				<button onClick={() => props.editTask(index)}>Edit</button>
			</th>
		</tr> 
		)
	})
	return <tbody>{ret_map}</tbody>
}
export default Todo;
