import './index.css';
import React, { useState } from 'react';
import Backdrop from './Backdrop.js'
import Modal from './Modal.js'

function Todo() {

	const [modalisOpen, setModalIsOpen] = useState(false);

	function activateModal() {
		this.setModalIsOpen(true)
	}

	function desactivateModal(index) {
		this.setModalIsOpen(false)
	}

	function deleteHandler(index) {
		this.activateModal()
	}

  	function render() {
	  	const render_tab = this.props.task_data.map((data, index) => {
		const retgreen = (
			<div className='greencard' key={index}>
				<h2>{data.task_name}</h2>
				<h3>{data.task_description}</h3>
				<div className='actions'>
					<button className='btn-green' onClick={() => this.deleteHandler(index)}>Delete</button>
					<button className='btn-green' onClick={() => this.deleteHandler(index)}>Edit</button>
				</div>
				{this.state.ModalisOpen && <Modal />}
				{this.state.ModalisOpen && <Backdrop closeModal={this.desactivateModal(index)}/>}
	 		</div>
		)

		const retred = (	
			<div className='redcard' key={index}>
				<h2>{data.task_name}</h2>
				<h3>{data.task_description}</h3>
				<div className='actions'>
			  		<button className='btn-red' onClick={() => this.deleteHandler(index)}>Delete</button>
					<button className='btn-red' onClick={() => this.deleteHandler(index)}>Edit</button>
				</div>
				{this.state.ModalisOpen && <Modal />}
				{this.state.ModalisOpen && <Backdrop onCancel={this.desactivateModal(index)}/>}
	 		</div>
		)
		if(data.task_status === 'done')
			return retgreen
		return retred
	  })
	}

	return <div>{render()}</div>

}

export default Todo;
