import './index.css';
import Todo from './Todo.js'
import { Component } from 'react';

class App extends Component {
	state = {
		all_task: [
			{
				task_name: 'Cook',
				task_status: 'Ongoing',
        task_description: 'Faire cuire les olives',
        task_id:'0'
			},
		]
	}

	removeTask = (index) => {
		const {all_task} = this.state

		this.setState({
			all_task: all_task.filter((task, i) => {
				return i !== index
			}),
		})
	}

	AddTask = (task) => {
		this.setState({all_task: [...this.state.all_task, task]})
	}

  render() {  
    const {all_task} = this.state
    return (
      <div>
        <h1>My todos</h1>
        <Todo all_task={all_task} removeFunction={this.removeTask}/>
      </div>
    )
  }
}

export default App;
